public class Solution {
    public int NumberOf1Between1AndN_Solution(int n) {
        int digit = 1, res = 0;
        int hight = n / 10, cur = n % 10, low = 0;
        while (hight != 0 || cur != 0) {
            if (cur == 0) res += hight * digit;
            else if(cur == 1) res += hight * digit + low + 1;
            else res += (hight + 1) * digit;
            low += cur * digit;
            cur = hight % 10;
            hight /= 10;
            digit *= 10;
        }
        return res;
    }
}

