        for (int i=0; i= 0 && P2 > P1) {
            char d = str.charAt(P1--);
            if (d == ' ') {
                str.setCharAt(P2--, '0');
                str.setCharAt(P2--, '2');
                str.setCharAt(P2--, '%');
            } else {
                str.setCharAt(P2--, d);
            }
        }
